router.post('/sobot/fetch-messages', async ctx => {
  const uidList = require('./1-2.json');

  async function fetchMessagesWithConcurrency(concurrency) {
    const allMap = {};
    const uidsChunked = [];

    for (let i = 0; i < uidList.length; i += concurrency) {
      uidsChunked.push(uidList.slice(i, i + concurrency));
    }

    for (const uids of uidsChunked) {
