for (let i = 0; i < chunks; i++) {
  const start = i * chunkSize;
  const end = Math.min(start + chunkSize, newArr.length);
  const part = newArr.slice(start, end); // 截取数组的一部分
  const fileName = `part-${i + 1}.json`; // 为每个文件命名，如：part-1.json, part-2.json 等

  fs.writeFile(fileName, JSON.stringify(part), (err) => {
