import pymongo
from pymongo import MongoClient
import json
from collections import defaultdict

async def fetch_all_messages(skip=0, limit=100, model=None, sources=None, os=None):
    if model is None or sources is None or os is None:
        raise ValueError("Please provide 'model', 'sources', and 'os' arguments.")

    # Fetch a batch of conversations with valid cid
    cid_docs = await model.find(
        {
            "content.cid": {"$exists": True},
            "$or": [{"type": "user"}, {"type": "conversation"}],
        }
    ).skip(skip).limit(limit).to_list(None)

    cids = set()
    convos_metadata = {}

    # Retrieve all convos ids
    for doc in cid_docs:
        for item in doc["content"]:
            # Retrieve each convo id
            cids.add(item["cid"])
            # Retrieve useful metadata as well
            if doc["type"] == "conversation":
                convos_metadata[item["cid"]] = {
                    "source": sources[item["source"]],
                    "os": os[item["os"]],
                    "ip": item["ip"],
                    "area": item["area"],
                    "staffName": item["staffName"],
                }

    # Fetch all messages of these convos and sort them
    msg_docs = await model.find(
        {
            "type": "msg",
            "content.cid": {"$in": list(cids)},
        }
    ).sort([("content.timems", 1)]).to_list(None)

    # Flatten message array and remove duplicates
    msgs = []
    for doc in msg_docs:
        for item in doc["content"]:
            if item["cid"] not in cids:
                continue
            msgs.append(item)

    message_list = []
    prev = None
    for msg in msgs:
        if prev is None or json.dumps(prev) != json.dumps(msg):
            msg = add_message_identity(msg)
            message_list.append(msg)
            prev = {k: v for k, v in msg.items() if k != "_id"}

    return {
        "convos_metadata": convos_metadata,
        "message_list": message_list,
    }
