const Sobot = (module.exports = {});

// dependencies
const _ = require('lodash');
const chalk = require('chalk');
const request = require('request');
const Config = require('../config/config');
const MaimaiUser = require('../models/maimai-user');
const J = require('./helpers');
const crypto = require('crypto');
const qs = require('querystring');
const uint64 = require('cuint').UINT64;
const mongoose = require('mongoose');
const model = mongoose.model('sobots', new mongoose.Schema({}));

// To do conversion between uid and oauth id
const oauth_mask = 0b1000001010011010;
const oauth_shift = 5;

// Shift bits in a 32-bit system
const bitRotateShift32 = (mask_uid, loops, is_right) => {
  const high = uint64(0b10000000000000000000000000000000);
  mask_uid = uint64(mask_uid);
  for (let l = 0; l < loops; l++) {
    if (is_right) {
      const low_bit = mask_uid.clone().and(uint64(1));
      mask_uid.shiftRight(uint64(1));
      if (low_bit.clone().toNumber()) {
        mask_uid.or(high);
      }
    } else {
      const high_bit = mask_uid.clone().and(high);
      mask_uid.shiftLeft(uint64(1));
      if (high_bit.clone().toNumber()) {
        mask_uid.or(uint64(1));
      }
    }
  }
  if (!is_right) {
    mask_uid.and(uint64(0xffffffff));
  }
  return mask_uid;
};

// Uid to oauth id
const encodeUidToOauthId = uid => {
  uid = uint64(uid);
  uid.xor(uint64(oauth_mask));
  return bitRotateShift32(uid, oauth_shift, true).toString();
};

// Oauth id to uid
const decodeUidFromOauthId = oauth_id => {
  uid = uint64(oauth_id);
  const mask_uid = bitRotateShift32(oauth_id, oauth_shift, false);
  return mask_uid.xor(uint64(oauth_mask)).toNumber();
};

// add field indicating it's sent by customer service operator OR robot OR user
const addMessageIdentity = msg => {
  msg.is_operator = msg.senderType === 2;
  msg.is_robot = msg.senderType === 1;
  msg.is_user = msg.senderType === 0;
  return msg;
};

// Get access token
Sobot.getAccessToken = async () => {
  const md5 = crypto.createHash('md5');
  const createTime = Date.now();
  const expire = 24; // token expires in 24h
  const sign = md5
    .update(`${Config.sobot_app_id}${Config.sobot_app_key}${createTime}`)
    .digest('hex');
  const result = await J.get(
    'https://open.sobot.com/open/platform/getAccessToken.json?' +
      qs.stringify({
        sign,
        createTime,
        expire,
        appId: Config.sobot_app_id,
      })
  );
  if (result && result.code == '1000' && result.data.access_token) {
    return result.data.access_token;
  } else {
    throw new Error('Get Sobot access token fail: ' + JSON.stringify(result));
  }
};

// Fetch all conversations, ordered by latest to earliest
Sobot.fetchConvos = async start_time => {
  const query = {
    type: 'user',
    'content.maimai_uid': { $ne: null },
  };
  if (start_time) {
    query.time_received_by_webhook_server = { $lt: new Date(start_time) };
  }
  // fetch 50 latest conversations that have been created
  const cids = {};
  const fetch_count = 30;
  const user_docs = await model
    .find(query)
    .sort({ time_received_by_webhook_server: -1 })
    .limit(fetch_count)
    .lean();
  const user_docs_length = user_docs.length;
  _.each(user_docs, user => {
    const item = _.last(user.content);
    if (!item) return;
    // 我发现partnerId才是我的id
    cids[item.cid] = {
      cid: item.cid,
      uid: item.maimai_uid,
      uid2: item.partnerId,
    };
  });
  // mark the search time for next batch of infinite load
  let next_search_start_time;
  if (_.last(user_docs))
    next_search_start_time = new Date(
      _.last(user_docs).time_received_by_webhook_server
    ).toISOString();
  // for each convo
  const convo_list = [];
  const promises = [];
  for (const { cid, uid, uid2 } of _.values(cids)) {
    const promise = async () => {
      let latest_msg;
      try {
        // get the latest sent message
        const msg_doc = await model
          .findOne({
            type: 'msg',
            'content.cid': cid,
            'content.msg': { $ne: '' },
          })
          .sort({ 'content.timems': -1 })
          .lean();
        if (!msg_doc) return;

        latest_msg = _.last(msg_doc.content);
        if (!latest_msg) return;

        // and user info
        try {
          latest_msg.user_info = await MaimaiUser.fetchViaAPI(uid);
        } catch (e) {
          // 这是一个坑
          /**
           * 我们需要把partnerId通过一个转换，变成一个uid
           * 然而部分用户的uid就是partnerId，所以其实把这部分用户的uid破坏了
           * 所以在这边还能暂且补救，就是再用partnerId查一下
           */
          latest_msg.user_info = await MaimaiUser.fetchViaAPI(uid2);
        }
      } catch (e) {
        // console.log('errorrrr', e)
      }
      // only if latest message and user info are successfully fetched,
      // we append that convo
      if (latest_msg && latest_msg.user_info) {
        convo_list.push(addMessageIdentity(latest_msg));
      }
    };
    promises.push(promise);
  }
  await Promise.all(promises.map(fn => fn()));
  // sort convo by message time
  convo_list.sort((a, b) => b.timems - a.timems);
  return {
    convo_list,
    next_search_start_time,
    user_docs_length, // 还是有用户存在的只不过这些用户有的被筛掉了
  };
};

