const qs = require('querystring');
const uint64 = require('cuint').UINT64;
const mongoose = require('mongoose');
const model = mongoose.model('sobots', new mongoose.Schema({}));

// To do conversion between uid and oauth id
const oauth_mask = 0b1000001010011010;
const oauth_shift = 5;

// Shift bits in a 32-bit system
const bitRotateShift32 = (mask_uid, loops, is_right) => {
  const high = uint64(0b10000000000000000000000000000000);
  mask_uid = uint64(mask_uid);
  for (let l = 0; l < loops; l++) {
    if (is_right) {
      const low_bit = mask_uid.clone().and(uint64(1));
      mask_uid.shiftRight(uint64(1));
      if (low_bit.clone().toNumber()) {
        mask_uid.or(high);
      }
    } else {
      const high_bit = mask_uid.clone().and(high);
      mask_uid.shiftLeft(uint64(1));
      if (high_bit.clone().toNumber()) {
        mask_uid.or(uint64(1));
      }
    }
  }
  if (!is_right) {
    mask_uid.and(uint64(0xffffffff));
  }
  return mask_uid;
};

// Uid to oauth id
const encodeUidToOauthId = uid => {
  uid = uint64(uid);
  uid.xor(uint64(oauth_mask));
  return bitRotateShift32(uid, oauth_shift, true).toString();
};

// Oauth id to uid
const decodeUidFromOauthId = oauth_id => {
  uid = uint64(oauth_id);
  const mask_uid = bitRotateShift32(oauth_id, oauth_shift, false);
  return mask_uid.xor(uint64(oauth_mask)).toNumber();
};

// add field indicating it's sent by customer service operator OR robot OR user
const addMessageIdentity = msg => {
  msg.is_operator = msg.senderType === 2;
  msg.is_robot = msg.senderType === 1;
  msg.is_user = msg.senderType === 0;
  return msg;
};

// Get access token
Sobot.getAccessToken = async () => {
  const md5 = crypto.createHash('md5');
  const createTime = Date.now();
  const expire = 24; // token expires in 24h
  const sign = md5
    .update(`${Config.sobot_app_id}${Config.sobot_app_key}${createTime}`)
    .digest('hex');
  const result = await J.get(
    'https://open.sobot.com/open/platform/getAccessToken.json?' +
      qs.stringify({
        sign,
        createTime,
        expire,
        appId: Config.sobot_app_id,
      })
  );
  if (result && result.code == '1000' && result.data.access_token) {
    return result.data.access_token;
  } else {
    throw new Error('Get Sobot access token fail: ' + JSON.stringify(result));
  }
};

