	"io/ioutil"
	"net/http"
	"sync"
)

// 声明一个Result结构体，用于保存每个URL的响应结果
type Result struct {
	URL  string
	Body string
}

// fetchURL函数用于获取URL的响应结果，并将结果通过channel发送给主goroutine
func fetchURL(url string, ch chan<- Result, wg *sync.WaitGroup) {
	// 声明函数完成后要调用的函数
	defer wg.Done()
	// 使用http包中的Get函数获取URL的响应结果
	resp, err := http.Get(url)
	// 如果获取失败则打印错误信息，并返回
	if err != nil {
		fmt.Printf("Error fetching URL %s: %v\n", url, err)
		return
	}
	// 确保在函数退出前关闭响应的body
	defer resp.Body.Close()

	// 读取响应结果的body
	bodyBytes, err := ioutil.ReadAll(resp.Body)
	// 如果读取失败则打印错误信息，并返回
	if err != nil {
		fmt.Printf("Error reading response body for URL %s: %v\n", url, err)
		return
	}

	// 将结果发送到channel中
	ch <- Result{URL: url, Body: string(bodyBytes)}
}

func main() {
	// 要获取的URL列表
	urls := []string{
		"https://www.example.com",
		"https://www.example.org",
		"https://www.example.net",
	}

	// 声明等待组
	var wg sync.WaitGroup
	// 创建用于传递结果的channel
	ch := make(chan Result)

	// 循环遍历URL列表，为每个URL创建一个goroutine来获取其响应结果
	for _, url := range urls {
		// 通知等待组有一个任务要完成
		wg.Add(1)
		// 创建goroutine并调用fetchURL函数
		go fetchURL(url, ch, &wg)
	}

	// 创建一个匿名的goroutine来等待所有任务完成后关闭channel
	go func() {
		wg.Wait()
		close(ch)
	}()

	// 使用range语句从channel中读取每个结果并打印结果
	for result := range ch {
		fmt.Printf("Fetched URL: %s\n", result.URL)
		fmt.Printf("Content length: %d\n", len(result.Body))
		fmt.Println("-----------")
	}
}
