package main

import (
	"flag"
	"fmt"
	"io/ioutil"
	"os"
	"path/filepath"
)

func main() {
	// 解析命令行参数
	dirPath := flag.String("dir", ".", "the path of the directory to scan")
	flag.Parse()

	// 获取指定目录下的所有文件
	files, err := ioutil.ReadDir(*dirPath)
	if err != nil {
