// 处理博客文章提交请求
func (blog *Blog) handleSubmit(w http.ResponseWriter, r *http.Request) error {
	// 解析请求中的博客文章信息
	err := r.ParseForm()
	if err != nil {
		return fmt.Errorf("failed to parse form data: %w", err)
	}
	idStr := r.Form.Get("id")
	title := r.Form.Get("title")
	content := r.Form.Get("content")

	// 如果有 ID，更新对应的博客文章
	if idStr != "" {
		id, err := strconv.Atoi(idStr)
		if err != nil {
			return fmt.Errorf("invalid article ID: %w", err)
		}
		_, err = blog.db.Exec("UPDATE articles SET title = ?, content = ? WHERE id = ?", title, content, id)
		if err != nil {
			return fmt.Errorf("failed to update article: %w", err)
		}
	} else {
		// 否则创建一个新的博客文章
		_, err = blog.db.Exec("INSERT INTO articles (title, content) VALUES (?, ?)", title, content)
		if err != nil {
			return fmt.Errorf("failed to create article: %w", err)
		}
	}

	// 重定向到博客文章列表页面
	http.Redirect(w, r, "/", http.StatusFound)

	return nil
}

// 处理博客文章删除请求
func (blog *Blog) handleDelete(w http.ResponseWriter, r *http.Request) error {
	// 获取博客文章 ID
	idStr := r.URL.Query().Get("id")
	id, err := strconv.Atoi(idStr)
	if err != nil {
		return fmt.Errorf("invalid article ID: %w", err)
	}

	// 删除指定 ID 的博客文章
	_, err = blog.db.Exec("DELETE FROM articles WHERE id = ?", id)
	if err != nil {
		return fmt.Errorf("failed to delete article: %w", err)
	}

	// 重定向到博客文章列表页面
	http.Redirect(w, r, "/", http.StatusFound)

	return nil
}

// 启动博客 Web 服务
func (blog *Blog) Run() error {
	http.HandleFunc("/", func(w http.ResponseWriter, r *http.Request) {
