  host: '<host>',
  user: '<user>',
  password: '<password>',
  database: '<database>'
});

dbClient.connect();

const model = 'text-davinci-002';
const prompt = 'What is the capital of France?';

async function generateText(model, prompt) {
  return new Promise((resolve, reject) => {
    openai.prompt(model, prompt, (err, response) => {
      if (err) {
        reject(err);
        return;
      }
      resolve(response.choices[0].text);
    });
  });
}

async function getCompletion(prompt) {
  const completion = await generateText(model, prompt);
  return completion;
}

async function saveCompletionToDb(completion) {
  return new Promise((resolve, reject) => {
    const query = `
      INSERT INTO completions (completion)
      VALUES ($1)
    `;
    const values = [completion];

    dbClient.query(query, values, (err, res) => {
      if (err) {
        reject(err);
        return;
      }
      resolve();
    });
  });
}

