    const completion = await getCompletion(prompt);
    await saveCompletionToDb(completion);
    console.log(`The completion is: ${completion}`);
  } catch (err) {
    console.error('Error occurred while generating the text', err);
  } finally {
    dbClient.end();
  }
}

